    FormInitializer.remoteSelectize($(&quot;.client_id&quot;), {}, {url: &quot;&#x2F;clients&quot;});

    $(&quot;.task_type&quot;).selectize();

    $(&quot;.create-task&quot;).click(function(){
      var client_id = $(&quot;.client_id&quot;).val();
