        $(&quot;.client_id&quot;).closest(&quot;.form-group&quot;).find(&quot;.help-block&quot;).removeClass(&quot;hidden&quot;);
      }

      if(!_.isEmpty(client_id) &amp;amp;&amp;amp; !_.isEmpty(task_type)){
        var url = &quot;&#x2F;clients&#x2F;&quot; + client_id;

        if(!_.isEmpty(assignment_id)){
          url += &quot;&#x2F;campaign&#x2F;assignments&#x2F;&quot; + assignment_id;
