      var assignment_id = $(e.relatedTarget).data(&#x27;assignment-id&#x27;);

      $(&quot;select.client_id, input[name=&#x27;assignment_id&#x27;]&quot;).val(&quot;&quot;);

      if(!_.isEmpty(assignment_id)){
        $(&#x27;input[name=&quot;assignment_id&quot;]&#x27;).val(assignment_id);
