        $(&quot;select.client_id&quot;).data(&quot;selectize&quot;).setValue(client_id);
      }
    });

    FormInitializer.remoteSelectize($(&quot;.client_id&quot;), {}, {url: &quot;&#x2F;clients&quot;});

    $(&quot;.task_type&quot;).selectize();

    $(&quot;.create-task&quot;).click(function(){
      var client_id = $(&quot;.client_id&quot;).val();
      var task_type = $(&quot;.task_type&quot;).val();
      var assignment_id = $(&quot;.assignment_id&quot;).val();
      $(&quot;.client_id, .task_type&quot;).closest(&quot;.form-group&quot;).removeClass(&quot;has-error&quot;);
      $(&quot;.client_id, .task_type&quot;).closest(&quot;.form-group&quot;).find(&quot;.help-block&quot;).addClass(&quot;hidden&quot;);
      if(_.isEmpty(client_id)){
        $(&quot;.client_id&quot;).closest(&quot;.form-group&quot;).addClass(&quot;has-error&quot;);
        $(&quot;.client_id&quot;).closest(&quot;.form-group&quot;).find(&quot;.help-block&quot;).removeClass(&quot;hidden&quot;);
      }

      if(!_.isEmpty(client_id) &amp;amp;&amp;amp; !_.isEmpty(task_type)){
        var url = &quot;&#x2F;clients&#x2F;&quot; + client_id;

        if(!_.isEmpty(assignment_id)){
          url += &quot;&#x2F;campaign&#x2F;assignments&#x2F;&quot; + assignment_id;
        }

        url += task_type == &quot;meeting&quot; ? &quot;&#x2F;meetings&#x2F;new&quot; : &quot;&#x2F;&quot; + task_type + &quot;&#x2F;tasks&#x2F;new&quot;
        $(&quot;.create-task&quot;).attr(&quot;href&quot;, url);
      }else{
        $(&quot;.create-task&quot;).attr(&quot;href&quot;, &quot;javascript:;&quot;);
      }
    })
    $(&quot;#menu-toggle&quot;).click(function(e) {
        e.preventDefault();
        $(&quot;#wrapper&quot;).toggleClass(&quot;toggled&quot;);
    });
