&lt;script&gt;
&#x2F;&#x2F;&amp;lt;![CDATA[

  $(document).ready(function(){
    var total_height = $(&quot;.navbar&quot;).height() + $(&quot;.navigation-links-container&quot;).height();
    $(&#x27;#task-form-modal&#x27;).on(&#x27;shown.bs.modal&#x27;, function(e){

      var client_id = $(e.relatedTarget).data(&#x27;client-id&#x27;);
      var client_name = $(e.relatedTarget).data(&#x27;client-name&#x27;);
      var assignment_id = $(e.relatedTarget).data(&#x27;assignment-id&#x27;);

      $(&quot;select.client_id, input[name=&#x27;assignment_id&#x27;]&quot;).val(&quot;&quot;);

      if(!_.isEmpty(assignment_id)){
        $(&#x27;input[name=&quot;assignment_id&quot;]&#x27;).val(assignment_id);
      }
      if(!_.isEmpty(client_id) &amp;amp;&amp;amp; !_.isEmpty(client_name)){
        $(&quot;select.client_id&quot;).data(&quot;selectize&quot;).addOption({id: client_id, name: client_name})
        $(&quot;select.client_id&quot;).data(&quot;selectize&quot;).setValue(client_id);
      }
    });

    FormInitializer.remoteSelectize($(&quot;.client_id&quot;), {}, {url: &quot;&#x2F;clients&quot;});

    $(&quot;.task_type&quot;).selectize();

    $(&quot;.create-task&quot;).click(function(){
      var client_id = $(&quot;.client_id&quot;).val();
      var task_type = $(&quot;.task_type&quot;).val();
      var assignment_id = $(&quot;.assignment_id&quot;).val();
      $(&quot;.client_id, .task_type&quot;).closest(&quot;.form-group&quot;).removeClass(&quot;has-error&quot;);
      $(&quot;.client_id, .task_type&quot;).closest(&quot;.form-group&quot;).find(&quot;.help-block&quot;).addClass(&quot;hidden&quot;);
      if(_.isEmpty(client_id)){
        $(&quot;.client_id&quot;).closest(&quot;.form-group&quot;).addClass(&quot;has-error&quot;);
        $(&quot;.client_id&quot;).closest(&quot;.form-group&quot;).find(&quot;.help-block&quot;).removeClass(&quot;hidden&quot;);
      }

      if(!_.isEmpty(client_id) &amp;amp;&amp;amp; !_.isEmpty(task_type)){
        var url = &quot;&#x2F;clients&#x2F;&quot; + client_id;

        if(!_.isEmpty(assignment_id)){
          url += &quot;&#x2F;campaign&#x2F;assignments&#x2F;&quot; + assignment_id;
        }

        url += task_type == &quot;meeting&quot; ? &quot;&#x2F;meetings&#x2F;new&quot; : &quot;&#x2F;&quot; + task_type + &quot;&#x2F;tasks&#x2F;new&quot;
        $(&quot;.create-task&quot;).attr(&quot;href&quot;, url);
      }else{
        $(&quot;.create-task&quot;).attr(&quot;href&quot;, &quot;javascript:;&quot;);
      }
    })
    $(&quot;#menu-toggle&quot;).click(function(e) {
        e.preventDefault();
        $(&quot;#wrapper&quot;).toggleClass(&quot;toggled&quot;);
    });
     $(&quot;#menu-toggle-2&quot;).click(function(e) {
        e.preventDefault();
        $(&quot;#wrapper&quot;).toggleClass(&quot;toggled-2&quot;);
        $(&#x27;#menu ul&#x27;).hide();
    });

     function initMenu() {
      $(&#x27;#menu ul&#x27;).hide();
      $(&#x27;#menu ul&#x27;).children(&#x27;.current&#x27;).parent().show();
      &#x2F;&#x2F;$(&#x27;#menu ul:first&#x27;).show();
      $(&#x27;#menu li a&#x27;).click(
        function() {
          var checkElement = $(this).next();
          if((checkElement.is(&#x27;ul&#x27;)) &amp;amp;&amp;amp; (checkElement.is(&#x27;:visible&#x27;))) {
            return false;
            }
          if((checkElement.is(&#x27;ul&#x27;)) &amp;amp;&amp;amp; (!checkElement.is(&#x27;:visible&#x27;))) {
            $(&#x27;#menu ul:visible&#x27;).slideUp(&#x27;normal&#x27;);
            checkElement.slideDown(&#x27;normal&#x27;);
            return false;
            }
          }
        );
      }
    initMenu();
    $(&#x27;#assignment-form-modal select.client_id&#x27;).on(&quot;change&quot;, function(e){
      var client_id = $(this).val();
      if(!_.isEmpty(client_id)){
        $(&quot;.create-project&quot;).attr(&quot;href&quot;, &quot;&#x2F;clients&#x2F;&quot; + client_id + &quot;&#x2F;campaign&#x2F;assignments&#x2F;new&quot;);
      }else{
        $(&quot;.create-project&quot;).attr(&quot;href&quot;, &quot;javascript:;&quot;);
      }
    });

    var notice_html = &quot;&quot;;
    if(!_.isEmpty(notice_html)){
      new Noty({
        text: notice_html,
        type: &#x27;success&#x27;,
        timeout: 10000
      }).show();
    }

    var alert_html = &quot;&quot;;
    if(!_.isEmpty(alert_html)){
      new Noty({
        text: alert_html,
        type: &#x27;error&#x27;,
        timeout: 10000
      }).show();
    }
  });

